import './App.css';
import { incBonususer } from './action';
import Account from './components/Account';
import Bonus from './components/Bonus';
import { useState } from 'react';
function App({store}) {
  const [account, setAccount] = useState({ amount: 0 });

   const increment = () => {
    setAccount({ amount: account.amount + 1 });
  };

  const decrement = () => {
    setAccount({ amount: account.amount - 1 });
  };

  const incrementByAmount = (value) => {
    setAccount({ amount: account.amount + value });
  };


  const [bonus, setBonus] = useState({ price: 0 });

  const incrementBonus = () => {
    setBonus({ points: bonus.points + 1 });
  };

  return (
    <div className="App">
      <h4>App</h4>
      <h3>Current Amount : {store.getState().account.amount}</h3>
      <h3>Total Bonus :{store.getState().bonus.price} </h3>

      <Account account={account} increment={increment} decrement={decrement} incrementByAmount={incrementByAmount}></Account>
      <Bonus store={store} incBonususer={incBonususer} ></Bonus>
    </div>
  );
}

export default App;
