import { incBonus, incacc } from "../actions";


export function bonusReducer(state = { price: 0 }, action) {
  switch (action.type) {
    case incBonus:
      return { price: state.price + 1 };
    case incacc:
      if (action.payload <= 100) {
        return { price: state.price };
      }
      return { price: state.price + action.payload };
    default:
      return state;
  }
}
